// Set Laoding
let tableProgress = document.querySelector('.datable-loading');
let aeLoading = document.querySelector('.ae-loading');
function setLoading() {
    tableProgress.classList.remove('hide');
}
function aeSetLoading() {
    aeLoading.classList.remove('hide');
}

// Clear Laoding
function clearLoading() {
    tableProgress.classList.add('hide');
}
function aeClearLoading() {
    aeLoading.classList.add('hide');
}

// Completed Loading
function completedLoading() {
    let timer = null;
    
    if (timer) { clearTimeout(timer) }
    timer = setTimeout(function () {
        clearLoading();
    }, timer);
}

function aeCompletedLoading() {
    let timer = null;

    if (timer) { clearTimeout(timer) }
    timer = setTimeout(function () {
        aeClearLoading();
    }, timer);
}